document.querySelector("i.fa-solid.fa-right-left").addEventListener("click",()=>{document.querySelector(".sidebar").classList.toggle("no-sidebar")});let togglebtn=Array.from(document.querySelectorAll(".togglemenu"));togglebtn.forEach(e=>{e.addEventListener("click",e=>{e.currentTarget.classList.toggle("down"),document.querySelector(e.currentTarget.dataset.cont).classList.toggle("open")})}),document.querySelector(".fullscreen-toggle").addEventListener("click",e=>{e.currentTarget.classList.toggle("fullscreen"),e.currentTarget.classList.contains("fullscreen")?openFullscreen():(document.exitFullscreen(),console.log("right"))});var elem=document.documentElement;function openFullscreen(){elem.requestFullscreen?elem.requestFullscreen():elem.webkitRequestFullscreen?elem.webkitRequestFullscreen():elem.msRequestFullscreen&&elem.msRequestFullscreen()}document.querySelector(".toggle-setting").addEventListener("click",e=>{document.querySelector(".toggle-setting i").classList.toggle("fa-spin"),document.querySelector(".setting-box").classList.toggle("hide")});var toggleClasses=[];document.querySelectorAll(".colors-option li").forEach(e=>{toggleClasses.push(e.dataset.theme)});var themeToggle=Array.from(document.querySelectorAll(".colors-option li"));themeToggle.forEach(e=>{e.addEventListener("click",e=>{document.body.classList.remove(...toggleClasses),document.body.classList.add(e.currentTarget.dataset.theme),document.querySelectorAll(".colors-option li").forEach(e=>{e.classList.remove("active")}),e.currentTarget.classList.add("active")})});