document.querySelector("i.fa-solid.fa-right-left").addEventListener("click",()=>{document.querySelector(".sidebar").classList.toggle("no-sidebar")});let togglebtn=Array.from(document.querySelectorAll(".togglemenu"));togglebtn.forEach(e=>{e.addEventListener("click",e=>{e.currentTarget.classList.toggle("down"),document.querySelector(e.currentTarget.dataset.cont).classList.toggle("open")})}),document.querySelector(".fullscreen-toggle").addEventListener("click",e=>{e.currentTarget.classList.toggle("fullscreen"),e.currentTarget.classList.contains("fullscreen")?openFullscreen():(document.exitFullscreen(),console.log("right"))});var elem=document.documentElement;function openFullscreen(){elem.requestFullscreen?elem.requestFullscreen():elem.webkitRequestFullscreen?elem.webkitRequestFullscreen():elem.msRequestFullscreen&&elem.msRequestFullscreen()}